import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

//The Square component renders a single <button> and the Board renders 9 squares
//The Game component renders a board with placeholder values 

class Square extends React.Component {
  // //to remember what was clicked on, we use "state" to remember things
  // //set this.state in the component's constructor 
  // constructor(props){
  //   super(props); //in JS classes, always call super when defining the constructor 
  //   this.state = {
  //     value: null,
  //   };
  // }
  render() {
    return (
    // By calling this.setState from an onClick handler in the Square’s render method, 
    // we tell React to re-render that Square whenever its <button> is clicked.
    // the Square’s this.state.value will be 'X', so we’ll see the X on the game board. If you click on any Square, an X should show up
      <button 
        className="square" 
        onClick={() => this.props.onClick()}
      >
        {this.props.value}
      </button>
    );
  }
}

class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(9).fill(null),
    };
  }

  handleClick(i){
    const squares = this.state.squares.slice(); 
    squares[i] = 'X';
    this.setState({squares: squares}); 
  }

  renderSquare(i) {
    return (
        <Square 
         value={this.state.squares[i]} 
         onClick={() => this.handleClick(i)} 
       />
    );
  }

  render() {
    const status = 'Next player: X';

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}

class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

// ========================================

ReactDOM.render(
  <Game />,
  document.getElementById('root')
);
